import React, { useState } from 'react'

export default function Input() {
    const [name,setname]=useState("John")
    const [email,setemail]=useState("@gmail.com")
    const [password,setpassword]=useState("gh@123")
    const [dob,setdob]=useState("20-09-24")
    const [mobnum,setmobnum]=useState("999999999")
    const [gender,setgender]=useState("Male/Female")
    const [role,setrole]=useState("DEV")
       
  return (
    <div>
   <label className='name'>Name:</label>
   <Input type="name" name="name" id="username" onChange={(e)=>setname(e.target.value)}  required />
   <p>{name ?name:<p>please enter your name</p>}</p>

   <label className='email'>Email:</label>
   <Input type="email" name="email" id="email" onChange={(e)=>setemail(e.target.value)}  required />
   <p>{email ?email:<p>please enter your email</p>}</p>

   <label className='password'>password:</label>
   <Input type="password" name="password" id="password" onChange={(e)=>setpassword(e.target.value)}  required />
   <p>{password ?password:<p>please enter your password</p>}</p>

   <label className='dob'>DOB:</label>
   <Input type="date" name="date" id="date" onChange={(e)=>setdob(e.target.value)}  required />
   <p>{dob ?dob:<p>please enter your date of birth</p>}</p>

   <label className='mob'>Mobile_Number:</label>
   <Input type="number" name="number" id="number" onChange={(e)=>setmobnum(e.target.value)}  required />
   <p>{mobnum ?mobnum:<p>please enter your mobile number</p>}</p>

   <label className='gender'>Select Gender:</label>
   <p>Male<Input type="radio" name="gender" id="gender" onChange={(e)=>setgender(e.target.value)}  required /></p>
   <p>Female<Input type="radio" name="gender" id="gender" onChange={(e)=>setgender(e.target.value)}  required /></p>
   <p>{gender ?gender:<p>please select youe gender</p>}</p>

   <label className='role'>Enter Your Role:</label>
   <Input type="text" name="role" id="role" onChange={(e)=>setrole(e.target.value)}  required />
   <p>{role ?role:<p>please enter your </p>}</p>

    </div>
  )
}
